sys_variable_value.value:
(function execute(inputs, outputs) {  
  var status = sn_discovery.DiscoveryAPI.discoverIpAddress( inputs.ip_address, inputs.mid_application , inputs.source);
  var statusGr = new GlideRecord("discovery_status");
  if(statusGr.get(status)){
	  outputs.discovery_status = statusGr;
  }else{
	  outputs.discovery_status = null;
  }
})(inputs, outputs);

